import React from 'react'; 

function InfoTooltip(isOpen, isSuccess, resultText, onClose) { 
    const popupClass = `popup popup_type_info ${isOpen ? 'popup_opened' : ''}`; 
    const iconClass = isSuccess ? 'popup__info-icon' : 'popup__info-icon popup__info-icon_type_error'; 
  
    return (
      <section className={popupClass}> 
        <div className="popup__container"> 
          <figure className={iconClass}></figure> 
          <p className="popup__info-text">{resultText}</p> 
          <button type="button" className="popup__close-button" onClick={onClose} aria-label="Закрыть"></button>
        </div> 
      </section> 
    ); 
  } 
  
  export default InfoTooltip;
