import React from 'react';

function Login(props) {

    return (
        <section className="auth-container">
            <h3 className="auth-container__title">Вход</h3>
            <form action="#" className="form" name={`${props.name}-auth`}>
                <div className="form__text-wrap">
                    <input required="" type="text" name="name" className="form__text form__text_type_auth" placeholder="Название" id="place-title-input" minLength="2" maxlength="30" value="" />
                    <span className="form__error place-title-input-error"></span>
                </div>
                <div class="form__text-wrap">
                    <input required="" type="url" name="link" className="form__text form__text_type_auth" placeholder="Ссылка на картинку" id="link-input" value="" />
                    <span className="form__error link-input-error"></span>
                </div>
                <button type="submit" className="form__button form__button_type_auth">Войти</button>
            </form>
        </section>
    );

}

export default Login;
